<?php include_once('header.php');?>	<div class="main-content">		<div class="breadcrumbs" id="breadcrumbs">			<script type="text/javascript">				try{ace.settings.check('breadcrumbs' , 'fixed')}catch(e){}			</script>			<ul class="breadcrumb">				<li><a href="/user_admin/welcome"><i class="icon-home home-icon"></i>首页</a></li>				<li>修改密码</li>			</ul>		</div>		<div class="page-content">			<div class="page-header">				<h1 class="blue"><i class="icon-edit"></i> 修改用户密码</h1>			</div>			<style>				.form-group{height:34px;}			</style>			<div class="row">				<div class=".container" style="margin:50px;width:95%;">					<form class="form-horizontal" role="form">					  <input type="hidden" class="form-control" style="width:250px;" id="id" value="<?=$user_info['admin']['id']?>" readonly/>					  <div class="form-group">					    <label for="username" class="col-sm-2 control-label">用户帐号</label>					    <div class="col-sm-10">					      <input type="text" class="form-control" style="width:250px;" id="username" value="<?=$user_info['admin']['username']?>" readonly/>					    </div>					  </div>					  <div class="form-group">					    <label for="oldpass" class="col-sm-2 control-label">旧密码</label>					    <div class="col-sm-10">					      <input type="password" class="form-control" style="width:250px;" id="oldpass" placeholder="旧密码"><span id="oldpassTip" style="display:none;color:red;"></span>					    </div>					  </div>					  <div class="form-group">					    <label for="newpass" class="col-sm-2 control-label">新密码</label>					    <div class="col-sm-10">					      <input type="password" class="form-control" style="width:250px;" id="newpass" placeholder="新密码"><span id="newpassTip" style="display:none;color:red;"></span>					    </div>					  </div>					  <div class="form-group">					    <label for="newpassAgain" class="col-sm-2 control-label">确认新密码</label>					    <div class="col-sm-10">					      <input type="password" class="form-control" style="width:250px;" id="newpassAgain" placeholder="确认密码"><span id="newpassAgainTip" style="display:none;color:red;"></span>					    </div>					  </div>					  <div class="form-group">					    <label class="col-sm-2 control-label">  </label>					 	 <button type="submit" class="btn btn-primary" id="submit" style="text-align:center;">确认修改</button>					  </div>					</form>				</div>			</div>		</div>															</div>	<link rel="stylesheet" href="/public_source/www/assets/css/ace.min.css" />	<link  rel="stylesheet" href="/public_source/www/assets/css/jquery-confirm.css" />	<link  rel="stylesheet" href="/public_source/www/css/confirm.css" />    <script src="/public_source/www/assets/js/bootbox.min.js"></script>	<script src="/public_source/www/assets/js/jquery-confirm.js"></script>	<script type="text/javascript">	var admin_path = '<?php echo $admin_path;?>';	$(document).ready(function(){		var error = false;		$("#oldpass").blur(function() {			var oldpass = $("#oldpass").val();			var id= $("#id").val();			if(oldpass =='') {								showError('oldpass', '密码不能为空');				error = true;				return;			}			$.ajax({				url:"check_password",				type:"post",				data: {id:id,oldpass:oldpass},				dataType: 'json',				success:function(data) {					if(data.msg == 1) {						$("#oldpass").css({"border-color":"green"});						$("#oldpassTip").css({"display":"none"});						error = false;					}else {						showError('oldpass', '密码错误');						error = true;					}					}			});		});		$("#newpass").blur(function() {			var newpass = $("#newpass").val();			if(newpass == '') {				showError('newpass', '新密码不能为空');				error = true;			}else {				$("#newpass").css({"border-color":"green"});				$("#newpassTip").css({"display":"none"});			}		});		$("#newpassAgain").blur(function() {			var newpass = $("#newpass").val();			if(newpass == '') {				showError('newpass', '新密码不能为空');				error = true;				return;			}			var newpassAgain = $("#newpassAgain").val();			if(newpassAgain != newpass) {				showError('newpassAgain', '与输入的新密码不一致');				error = true;			}			else {				$("#newpassAgain").css({"border-color":"green"});				$("#newpassAgainTip").css({"display":"none"});			}		});				$("#submit").click(function(event ) {			$("#oldpass").blur();			$("#newpass").blur();			$("#newpassAgain").blur();			if(error == false) {				var id = $("#id").val();							var newpass = $("#newpass").val();				var oldpass = $("#oldpass").val();				$.ajax({					url:"edit_password",					type:"post",					data: {id:id,newpass:newpass,oldpass:oldpass},					dataType: 'json',					success:function(data) {						if(data.msg == 1) {						   	$.alert({							    title: '提示',							    content:'密码修改成功，请重新登录',							    confirm: function(){									window.location.replace(admin_path+'/login');									return false;							    }							});											}else {						   	$.alert({							    title: '提示',							    content:'密码修改失败，请重试',							    confirm: function(){							    }							});						}					}				});			}			event.preventDefault();			return false;		});	});	function showError(formSpan, errorText) {		$("#" + formSpan).css({"border-color":"red"});		$("#" + formSpan + "Tip").empty();		$("#" + formSpan + "Tip").append(errorText);;		$("#" + formSpan + "Tip").css({"display":"inline"});	}</script><?php include_once('footer.php');?>								